mapper.update:
UPDATE debugBoardDB
SET viewCount = viewCount + 1
WHERE boardNum = #{boardNum}

UPDATE debugBoardDB
SET 
    recommendNum = #{recommendNum}
WHERE boardNum = #{boardNum}

mapper.select:
SELECT
	*
FROM 
	memberDB
WHERE 
	userName = #{userName}
	AND userSSN1 = #{ssn1}
	AND userSSN2 = #{ssn2}

SELECT
	*
FROM 
	memberDB
WHERE 
	userName = #{userName}
	AND userId = #{userId}
	AND userSSN1 = #{ssn1}
	AND userSSN2 = #{ssn2}

SELECT COUNT(*)
FROM memberDB
WHERE 
	userId = #{userId}
	AND userPw = #{userPw}

SELECT COUNT(*)
FROM memberDB
WHERE 
	userid = #{userid}
	AND nickname = #{nickname}

SELECT COUNT(*)
FROM memberDB
WHERE 
	userid = #{userid}
	AND userEmailId = #{userEmailId}
	AND userEmailDomain = #{userEmailDomain}

SELECT COUNT(*)
FROM memberDB
WHERE 
	userid = #{userid}
	AND userPhoneNum1 = #{userPhoneNum1}
	AND userPhoneNum2 = #{userPhoneNum2}
	AND userPhoneNum3 = #{userPhoneNum3}

mapper.insert:
INSERT INTO memberDB (
userId, userPw, userName, userSSN1, userSSN2, 
userGender, nickname, userPhoneNum1, userPhoneNum2, userPhoneNum3,
userEmailId, userEmailDomain
)
VALUES (
#{userId}, #{userPw}, #{userName}, #{userSSN1}, #{userSSN2},
#{userGender}, #{nickname}, #{userPhoneNum1}, #{userPhoneNum2},
#{userPhoneNum3}, #{userEmailId}, #{userEmailDomain}
)

mapper.select:
SELECT COUNT(*)
FROM memberDB
WHERE userId = #{userId}

SELECT COUNT(*)
FROM memberDB
WHERE nickname = #{nickname}

SELECT COUNT(*)
FROM memberDB
WHERE 
	userPhoneNum1 = #{userPhoneNum1}
	AND userPhoneNum2 = #{userPhoneNum2}
	AND userPhoneNum3 = #{userPhoneNum3}

SELECT COUNT(*)
