mapper.update:
 update memberDB
 set 
 nickname = #{newNickname}
where userId = #{userId}

 update memberDB
 set 
 restoreUserEmailId = #{newRestoreEmailId}
where userId = #{userId}

 update memberDB
 set 
 restoreUserEmailDomain = #{newRestoreUserEmailDomain}
where userId = #{userId}

mapper.select:
select postid, postBoard, userid, contid, postTitle, postDate, postEditDate, postText, nickname, categoryId
from POSTDB
where userId = #{userId}

select  comId, postId , comDate,  comText 
from commentDB
where userId = #{userId}

select count(*)
from memberDB
where userId = #{userId}
